import { Dimensions, View, Text, Button } from 'react-native';
import { useState } from 'react';


export default function Task(props) {
    const isCompleted = false
    const width = Dimensions.get('window').width
    const height = Dimensions.get('window').height
    const onClick = () => {
        isCompleted = true
        console.log("Here")
    }
    if (!isCompleted) {
        return (<View style = {{width: "70%"}}>
            <Text style = {{fontSize: 0.03 * height}}>
                {props.title}
            </Text>
            <Text style = {{alignSelf: "center",fontSize: 0.02 * height }}>
                Description: {props.description}
            </Text>
            <Button style = {{width: "40%" }} title = "Complete" onPress= {onClick}></Button>
        </View>)
    } else {
        return <View></View>
    }
}
